    );
    drawContours(
      baseImg,
      diffRemoved.contours,
      colorRemove,
      thickness,
      diffOverlayWeight,
      changeArea
    );
  }

  // merge the images
  const transparencySlider = document.getElementById("transparency");
  const transparency =
    transparencySlider != null ? parseFloat(transparencySlider.value) : 0;

  let diffImg = new cv.Mat();
  cv.addWeighted(
    compareImg,
    0.5 + transparency,
    baseImg,
    0.5 - transparency,
    0,
    diffImg,
  );

  cv.imshow("diff", diffImg);

  baseImg.delete();
  compareImg.delete();
  diffImg.delete();
}

